                <div class=\"w-50 mx-auto\">
                {{ form_start(form) }}
                {{ form_widget(form) }}
                <div class=\"\">
                    <button type=\"submit\" class=\"btn btn-primary\">Confirmez le nouveau password</button>
                    <a href=\"{{ path('user_show', {'firstName' : app.user.firstName}) }}\" class=\"btn btn-primary\">RETOUR</a>
                </div>
                {{ form_end(form) }}
                </div>
